const header=document.querySelector("header"),sectionOne=document.querySelector(".banner"),sectionOneOptions={rootMargin:"-500px 0px 0px 0px"},sectionOneObserver=new IntersectionObserver((function(e,n){e.forEach((e=>{e.isIntersecting?header.classList.remove("sticky"):header.classList.add("sticky")}))}),sectionOneOptions);sectionOneObserver.observe(sectionOne);const hamburger=document.querySelector(".hamburger-icon"),navlinks=document.querySelector(".nav-list-wrapper");function incrementValue(){var e=parseInt(document.getElementById("number").value,10);e=isNaN(e)?0:e,e++,document.getElementById("number").value=e}hamburger.addEventListener("click",(()=>{navlinks.classList.toggle("show"),hamburger.classList.toggle("close")}));